import React, { useState, useContext, useEffect } from 'react';
import './UserProfile.css'; // Import your CSS file here
import { FaUser } from 'react-icons/fa';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { UserContext } from '../Context/UserContext';

export const UserProfile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const { user, updateUser } = useContext(UserContext);
  const [updateuser, setUpdateUser] = useState({
    fullname: '',
    email: '',
    phone: '',
    address: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Initialize form fields with user data from context
    if (user) {
      setUpdateUser({
        fullname: user.fullname || '',
        email: user.email || '',
        phmonone: user.phone || '',
        address: user.address || ''
      });
    }
  }, [user]);

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdateUser({ ...updateuser, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const id = localStorage.getItem('userid');

    try {
      const response = await axios.put('http://localhost:9093/userdata/updateuser', {
        ...updateuser,
        id // Add the ID to the payload
      });

      if (response.status === 200) {
        console.log("Updated Successfully");
        updateUser(response.data); // Update context with new user data
      }
    } catch (error) {
      console.log(error);
    }

    setIsEditing(false);
  };

  const handleLogout = () => {
    localStorage.setItem('isAuthenticated', 'false');
    navigate("/");
  };

  return (
    <div className='profilesection'>
      <div className="user-profile">
        <h1>My Profile</h1>
        <div className="profile-info">
          <div className="profile-picture">
            <FaUser />
          </div>
          <div className="profile-details">
            {isEditing ? (
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label>Full Name:</label>
                  <input
                    type="text"
                    name="fullname"
                    value={updateuser.fullname}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  <input
                    type="email"
                    name="email"
                    value={updateuser.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Phone:</label>
                  <input
                    type="tel"
                    name="phone"
                    value={updateuser.phone}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label>Address:</label>
                  <input
                    type="text"
                    name="address"
                    value={updateuser.address}
                    onChange={handleChange}
                  />
                </div>
                <button type="submit">Save</button>
              </form>
            ) : (
              <div>
                <p><strong>Full Name:</strong> {user.fullname}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Phone:</strong> {user.mobno}</p>
                <p><strong>Address:</strong> {user.address}</p>
                <button className='usereditbutton' onClick={handleEditToggle}>Edit</button>
                <button className='user-logout' onClick={handleLogout}>Log Out</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
