// src/Components/LocationHandler.js
import React, { useState, useEffect } from 'react';

const Location = ({ onLocationSuccess, onLocationError }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const success = (position) => {
      const { latitude, longitude } = position.coords;
      onLocationSuccess({ latitude, longitude });
    };

    const error = (err) => {
      setError(err.message);
      onLocationError(err.message);
    };

    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(success, error);
    } else {
      setError('Geolocation is not supported by this browser.');
      onLocationError('Geolocation is not supported by this browser.');
    }

    setLoading(false);
  }, [onLocationSuccess, onLocationError]);

  if (loading) return <p>Loading location...</p>;
  if (error) return <p>Error: {error}</p>;

  return null; // Render nothing if location is successfully fetched
};

export default Location;
