import React from 'react';
import './Payment.css'; // Import your CSS file
import { useState } from 'react';
export const Payment = () => {
  const [PaymentMethod, setPaymentMethod] = useState('card')

  const handlePaymetOption = (event) => {
    setPaymentMethod(event.target.value)

  }
  return (
    <div>
      <h1>Make Payment</h1>
      <div className='payment-option'>
        <label>
          <input type='radio' name='payment' value="card"
            checked={PaymentMethod === 'card'}
            onChange={handlePaymetOption} />
          Credit Card/DebitCard
        </label>
        <label>
          <input type='radio' name='payment' value="UPI" checked={PaymentMethod === 'UPI'} onChange={handlePaymetOption} />
          UPI
        </label>
      </div>

    </div>
  );
};
