import './App.css';
import './index.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Navbar } from './Components/Navbar';
import { Signup } from './Components/Signup';
import { Login } from './Components/Pages/Login';
import { MyProfile } from './Components/Pages/MyProfile';
import { Bike } from './Components/Pages/Bike';
import { ShowBike } from './Components/Pages/ShowBike';
import LocationHandler from './Components/LocationHandler'; // Import the LocationHandler component

function App() {
  const [locationGranted, setLocationGranted] = useState(false);
  const [locationError, setLocationError] = useState(null);

  const handleLocationSuccess = (location) => {
    setLocationGranted(true);
    // Optionally, store location in state or context
  };

  const handleLocationError = (error) => {
    setLocationError(error);
  };

  if (!locationGranted) {
    return (
      <div>
        <LocationHandler
          onLocationSuccess={handleLocationSuccess}
          onLocationError={handleLocationError}
        />
      </div>
    );
  }

  return (
    <Router>
      <div id="RideRental">
        <Navbar />
        <div>
          <Routes>
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route path="/myprofile" element={<MyProfile />} />
            <Route path="/bike" element={<Bike />} />
            <Route path="/showbike" element={<ShowBike />} />
            <Route path="/" element={<Home />} /> {/* Optional: Home Route */}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
