import React, { useEffect, useState } from 'react';
import './HeroSection.css';
import axios from 'axios';

export const HeroSection = () => {
  const [data,setdata]=useState([]);
  const [showmessage,setshowmessage]=useState('');


 useEffect(()=>{
   const fetchdata=async()=>{
     try {
      const response=await axios.get('http://localhost:9093/bike');
      if(response.status===200){
        setdata(response.data);
      }else{
        setshowmessage("No Data Avialable")
        console.log("Not Responding",response.status);
      }
     } catch (error) {
       console.log("error in fetching data");
     }
   }
   fetchdata();
 },[])
  
 if(data.length===0)return <p>{showmessage}</p>;

  return (
    <div className='Herocontainer'>
      <h2>Available :</h2>
      <div>
        {
          data.forEach((product,index)=>(
            <div key={index}>
              <h3>{product.bikeName}</h3>
              <p>{product.bikeNumber}</p>
            </div>
          ))
        }
      </div>
    </div>
  );
};
