import React from 'react'
import { FaSearch,FaArrowAltCircleDown,FaArrowAltCircleUp } from 'react-icons/fa';

export const Navbar = () => {

    const [isHovered, setIsHovered] = useState(false);

    // Handlers for mouse events
    const handleMouseEnter = () => setIsHovered(true);
    const handleMouseLeave = () => setIsHovered(false);
    return (
        <div >
            <div></div>
            <div>
                <input type="search"></input>
                    <button>
                        <FaSearch />
                    </button>
             
            </div>
            <button
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        backgroundColor: isHovered ? 'blue' : 'gray', // Change color on hover
        color: 'white', // Button text color
        border: 'none',
        padding: '10px 20px',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        borderRadius: '5px'
      }}
    >
      Login
      {isHovered ? (
        <FaArrowAltCircleUp style={{ marginLeft: '8px' }} /> // Arrow up icon when hovered
      ) : (
        <FaArrowAltCircleDown style={{ marginLeft: '8px' }} /> // Arrow down icon otherwise
      )}
    </button>
        </div>
    )
}
