import React, { useEffect, useState } from 'react';
import './UserProfile.css'; // Import your CSS file here
import { FaUser } from 'react-icons/fa';
import axios from 'axios';

export const UserProfile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [userdata, setuserdata] = useState({
    
  });

  // Toggle editing mode
  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  // Handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };
  useEffect(()=>{
    const userid=localStorage.getItem('useid');
    const Fetchprofile=async()=>{
       const response=await axios.get('http://localhost:9093/userprofilefetch',{
        param:{
           id:userid

        }
       })
      if(response.status===200){
        setuserdata(response.data);
      }
    }
    Fetchprofile();
  })
  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit user data (e.g., via API)
    console.log('User profile updated:', user);
    setIsEditing(false);
  };

  return (
    <div className="user-profile">
      <h1>My Profile</h1>
      <div className="profile-info">
        <div className="profile-picture">
          <FaUser/>
        </div>
        <div className="profile-details">
          {isEditing ? (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Full Name:</label>
                <input
                  type="text"
                  name="fullname"
                  value={user.fullname}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Email:</label>
                <input
                  type="email"
                  name="email"
                  value={user.email}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Phone:</label>
                <input
                  type="tel"
                  name="phone"
                  value={user.phone}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Address:</label>
                <input
                  type="text"
                  name="address"
                  value={user.address}
                  onChange={handleChange}
                  required
                />
              </div>
              <button type="submit">Save</button>
            </form>
          ) : (
            <div>
              <p><strong>Full Name:</strong> {user.fullname}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Phone:</strong> {user.phone}</p>
              <p><strong>Address:</strong> {user.address}</p>
              <button className='usereditbutton' onClick={handleEditToggle}>Edit</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
