import React, { useEffect, useState } from 'react';
import './HeroSection.css';
import axios from 'axios';

export const HeroSection = () => {
  const [data, setData] = useState([]);  // Initialize as an empty array
  const [loading, setLoading] = useState(true);  // To handle loading state
  const [error, setError] = useState(null);  // To handle error state

  useEffect(() => {
    const dataFetch = async () => {
      try {
        const response = await axios.get('http://localhost:9093/bike');
        if (response.status === 200) {  // Check for status code 200
          setData(response.data);
        } else {
          console.log('Server responded with status:', response.status);
        }
      } catch (error) {
        setError(error);  // Set error if any
        console.log('Error fetching data:', error);
      } finally {
        setLoading(false);  // Set loading to false when done
      }
    };

    dataFetch();
  }, []);

  if (loading) return <p>Loading...</p>;  // Show loading message
  if (error) return <p>Error loading data: {error.message}</p>;  // Show error message
  if (data.length === 0) return <p>No data available</p>;  // Show if no data

  return (
    <div className='Herocontainer'>
      <h2>Available :</h2>
      <div>
        {data.map((product, index) => (
          <div key={index}>
            <h3>{product.bikeName}</h3>
            <p>{product.bikeNumber}</p>
            <p>{product.bikeModel}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
