import React, { useEffect, useState } from 'react';
import './HeroSection.css';
import axios from 'axios';

export const HeroSection = () => {
  const [data, setdata] = useState([]);
  const [showmessage, setshowmessage] = useState('Available:');
  const[count,setcount]=useState(1);

  const handleDecrement=()=>{
    if(count>1){
      setcount(prevCount => prevCount - 1)
    }
  }
  const handleIncrement=()=>{
    setcount(prevCount => prevCount + 1)

  }

  useEffect(() => {
    const fetchdata = async () => {
      try {
        const response = await axios.get('http://localhost:9093/bike');
        if (response.status === 200) {
          setdata(response.data);
          if (response.data.length === 0) {
            setshowmessage('No Data Available');
          }
        } else {
          setshowmessage('No Data Available');
          console.log('Not Responding', response.status);
        }
      } catch (error) {
        setshowmessage('Error fetching data');
        console.log('Error in fetching data', error);
      }

    }; 
    fetchdata();
  }, []);

  return (
    <div className='Herocontainer'>
      <div className='available'><h2>{showmessage}</h2></div>
      <div>
        {
          data.length > 0 && data.map((product, index) => (
            <div className='BikeData' key={index}>
              <h3>{product.bikeName}</h3>
              <p>{product.bikeNumber}</p>
              <p>{product.price}Per/Day</p>
              <div>
                <p>For How Many Days:<div ><button onClick={handleDecrement}>-</button>{count}<button onClick={handleIncrement}>+</button></div></p>
              </div>
              <input type='button' value='Book' className='bookbutton'></input>
            </div>
          ))
        }
      </div>
    </div>
  );
};
