import React, { useState } from 'react';
import './Navbar.css';
import { Option } from './Option';
import { FaSearch, FaArrowAltCircleDown, FaArrowAltCircleUp, FaList, FaCut } from 'react-icons/fa';

export const Navbar = () => {
    const [isHovered, setIsHovered] = useState(false);
    const [isClicked, setIsClicked] = useState(false);
    
    // Handlers for mouse events
    const handleMouseEnter = () => setIsHovered(true);
    const handleMouseLeave = () => setIsHovered(false);
    const handleClick = () => setIsClicked(prevState => !prevState); // Toggle state on click
   
    return (
        <div id='header'>
            <div id='logo-icon'>
                <button
                    onClick={handleClick}
                    
                >
                    {isClicked ? <FaCut /> : <FaList />}
                    
                </button>
                {isClicked ? <Option/> : ""}
            </div>
            <div id='name'>
               <h2>WELCOME RIDE-RENTAL</h2>
            </div>
            <div>
                <button
                    id='loginbutton'
                    onMouseEnter={handleMouseEnter}
                    onMouseLeave={handleMouseLeave}
                    aria-label="Login button"
                >
                    Login
                    {isHovered ? (
                        <FaArrowAltCircleUp id="icon" />
                    ) : (
                        <FaArrowAltCircleDown id="icon" />
                    )}
                </button>
            </div>
            <div>
                <button
                    id='loginbutton'
                    onMouseEnter={handleMouseEntersignup}
                    onMouseLeave={handleMouseLeave}
                    aria-label="Login button"
                >
                    Signup
                    {isHovered ? (
                        <FaArrowAltCircleUp id="icon" />
                    ) : (
                        <FaArrowAltCircleDown id="icon" />
                    )}
                </button>
            </div>
        </div>
    );
};
