import React, { useState } from 'react';
import { FaSearch, FaArrowAltCircleDown, FaArrowAltCircleUp } from 'react-icons/fa';

export const Navbar = () => {
    const [isHovered, setIsHovered] = useState(false);

    // Handlers for mouse events
    const handleMouseEnter = () => setIsHovered(true);
    const handleMouseLeave = () => setIsHovered(false);

    return (
        <div className="flex items-center p-4">
            <div className="flex-grow"></div>
            <div className="flex items-center space-x-2">
                <input
                    type="search"
                    className="p-2 border rounded-md border-gray-300"
                />
                <button className="p-2 bg-gray-200 rounded-md hover:bg-gray-300 transition-colors">
                    <FaSearch />
                </button>
            </div>
            <button
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                className={`ml-4 px-4 py-2  text-white transition-colors rounded-lg h ${
                    isHovered ? 'bg-blue-500' : 'bg-gray-500'
                } flex items-center`}
            >
                Login
                {isHovered ? (
                    <FaArrowAltCircleUp className="ml-2" />
                ) : (
                    <FaArrowAltCircleDown className="ml-2" />
                )}
            </button>
        </div>
    );
};
