import React, { useEffect, useState } from 'react';
import './HeroSection.css';
import axios from 'axios';

export const HeroSection = () => {
  const [data, setdata] = useState([]);
  const [showmessage, setshowmessage] = useState('Avialable :');


  useEffect(() => {
    const fetchdata = async () => {
      try {
        const response = await axios.get('http://localhost:9093/bike');
        if (response.status === 200) {
          setdata(response.data);
        } else {
          setshowmessage("No Data Avialable")
          console.log("Not Responding", response.status);
        }
      } catch (error) {
        console.log("error in fetching data");
      }
    }
    fetchdata();
  }, [])

  if (data.length === 0) return seshowmessage;

  return (
    <div className='Herocontainer'>
      <div className='available'><h2>{showmessage}</h2></div>
      <div>
        {
          data.map((product, index) => (
            <div key={index}>
              <h3>{product.bikeName}</h3>
              <p>{product.bikeNumber}</p>
            </div>
          ))
        }
      </div>
    </div>
  );
};
